import React from 'react';
import styled from "styled-components";
import Button from "../components/styled/Button.styled";

const ButtonContainer = styled.div`
  margin: 3rem 0 2rem 0;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
`;


const Container = styled.div`
  margin: 7rem 0 2rem 0;
  padding: 10%;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
`;

class Popup extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            time: 'start',
            title: 'Quiz',
            text: 'Spróbuj swoich sił rozwiązucjąc quiz.',
            buttonText: 'Rozpocznij quiz' 
        };

        this.popupHandle = this.popupHandle.bind(this);
    }

    popupHandle() {
        let { time } = this.state;

        if(time === 'start'){
            this.setState({
                time: 'end',
                title: 'Congratulations!',
                buttonText: 'Restart'
            });

            this.props.startQuiz();
        }
    }

    componentWillReceiveProps(nextProps) {
        this.setState({
            text: 'You have completed the quiz. <br /> You got: <strong>' + this.props.score + '</strong> out of <strong>' +this.props.total +'</strong> questions right.'
        })
    }

    createMarkup(text) {
        return {__html: text};
    }


    render() {

        let { title, text, buttonText } = this.state;

        let { style } = this.props;

        return (
            <div className="popup-container" style={style}>
                <div className="container">
                    <div className="col-md-8 col-md-offset-2">

                        <div className="popup">
                            <Container>
                                <h2>{title}</h2>
                            </Container>   
                            <Container>   
                                <p dangerouslySetInnerHTML={this.createMarkup(text)} />
                            </Container>   
                            <ButtonContainer>
                                <Button className="fancy-btn" onClick={this.popupHandle}>{buttonText}</Button>
                            </ButtonContainer>


                        </div>

                    </div>
                </div>
            </div>
        );
    }
}

export default Popup;
