import Button from "../components/styled/Button.styled";
import styled from "styled-components";




const ButtonContainer = styled.div`
  margin: 1rem 0 1rem 0;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
`;
var points = 0;

var answersBtns = document.querySelectorAll('.answer');

var checkPointsBtn = document.querySelector('.check-points');

var showCorrectAnswersBtn = document.querySelector('.show-answers');

var pointsContainer = document.querySelector('.points');

var pointsSpan = document.querySelector('.points span');

var clearBtn = document.querySelector('.clear');

var showAnswersBtn = document.querySelector('.show-answers');

function addPoints() {
  this.classList.add('selected');
  if(this.classList.contains('correct')) {
    points += 1;
  } 
}

function showPoints() {
  pointsSpan.innerHTML = points;
  pointsContainer.classList.add('show');
}

function clear() {
  pointsContainer.classList.remove('show');
  pointsContainer.classList.add('hide');

  points = 0;
  console.log(points);
  for(var i = 0; i < answersBtns.length; i++) {
    answersBtns[i].classList.remove('selected', 'show-correct');
  }
}

function showAnswers() {
  for(var i = 0; i < answersBtns.length; i++) {
    answersBtns[i].classList.remove('selected');
    if(answersBtns[i].classList.contains('correct'))     {
     answersBtns[i].classList.add('show-correct');
    }
  }
}

for(var i = 0; i < answersBtns.length; i++) {
  answersBtns[i].addEventListener('click', addPoints);
}

checkPointsBtn.addEventListener('click', showPoints);

clearBtn.addEventListener('click', clear);

showAnswersBtn.addEventListener('click', showAnswers);







function Quiz(){


    return(
      <div class="main-container">
        <h2>Quiz</h2>
        <div class="question-container">
          <div class="question">
            <p>Jakie miasto jest stolicą Francji?</p>
            <div class="answers">
            <ButtonContainer>
              <Button class="answer">Londyn</Button>
              <Button class="answer correct">Paryż</Button>
              <Button class="answer">Monachium</Button>
            </ButtonContainer>
            </div>
          </div>
          <div class="question">
            <p>Z jakiego języka pochodzi słowo <i>piżama</i>?</p>
            <div class="answers">
            <ButtonContainer>
              <Button class="answer">Francuski</Button>
              <Button class="answer correct">Hindi</Button>
              <Button class="answer">Portugalski</Button>
            </ButtonContainer>
            </div>
          </div>
          <div class="question">
            <p>Kto jako pierwszy opłynął kulę ziemską?</p>
            <div class="answers">
            <ButtonContainer>
              <Button class="answer">Krzysztof Kolumb</Button>
              <Button class="answer">Vasco da Gama</Button>
              <Button class="answer correct">Ferdynand Magellan</Button>
            </ButtonContainer>
            </div>
          </div>
          <div class="points-container">
            <button class="check-points big-btn">Sprawdzam odpowiedzi</button>
            <div class="points hide">Punkty: <span>0</span></div>
            <button class="show-answers big-btn">Pokaż poprawne odpowiedzi</button>
            <button class="clear big-btn">Zagraj jeszcze raz</button>
          </div>
        </div>
      </div>

    )


}

export default Quiz;
