import styled from "styled-components";
import { Colors, TextStyles } from "../../Theme";
import { Link } from 'react-router-dom';
import Navbar1 from "./DropdownMenu1.js";
import AvatarIcon from "../../images/AvatarIcon.png";
import NavbarUser from "./DropdownMenuUser.js";
import { Logo } from "./Header";
import {useDispatch, useSelector} from "react-redux";
import authSlice from "../../store/Auth";
import {useHistory} from "react-router";
import Button from "../styled/Button.styled";
import { useContext } from "react";
import { Context } from "../../../context/Context"; 



const HeaderEl = styled.header`
  padding: 2rem 0;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const LeftPart = styled.span`
  display: flex;
  column-gap: 1rem;
`;

const RightPart = styled.span`
  display: flex;
  column-gap: 1rem;
`;


const NavBar = styled.nav`
  display: flex;
  column-gap: 3.5rem;
  align-items: center;
  color: ${Colors.Brand.Text};
  ${TextStyles.Bundler(TextStyles.S1)}
`;
const NavItem = styled.a`
  :visited {
    color: ${Colors.Brand.Text};
  }
`;

const AvatartImage = styled.img`
  cursor: pointer;
  border-radius: 100px;
  border: 2px solid ${Colors.Brand.Button};
  `;

export default function HeaderUser( ) {
  const { user, dispatch } = useContext(Context);
  const history = useHistory();
  
  const handleLogout = () => {
    dispatch({ type: "LOGOUT" });
    history.push("/");
  };

  return (
    <HeaderEl>
      <LeftPart>
        <NavBar>
        <Link to="education">
          <NavItem>Edukacja</NavItem>
        </Link>
        <Link to="#" >
          <NavItem>Kategorie</NavItem>
        </Link>
          <Navbar1 />
        </NavBar>
      </LeftPart>
      {user ? (
        <RightPart>
        <Link to="/home">
          <Logo>FJM</Logo>
        </Link>
        <NavBar>
          <NavbarUser />		
          <Button onClick={handleLogout}>Wyloguj się</Button>	
        </NavBar>
        </RightPart>
        ) : (
        <RightPart>
        <NavBar>
          <NavItem href="/login">Zaloguj się</NavItem>
          
						<Link to="register">
							<Button>Zarejestruj się</Button>
						</Link>
					
        </NavBar>
        </RightPart>
        )}
    </HeaderEl>
  );
}
