import axios from "axios";
import React from "react";



export default function App() {
  const [post, setPost] = React.useState(null);

  React.useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/fiszki/5').then((response) => {
      setPost(response.data);
    });
  }, []);

  function createPost() {
    axios
      .post('http://127.0.0.1:8000/api/fiszki/', {
        text: "Hello World!",
        reverse: "This is a new post."
      })
      .then((response) => {
        setPost(response.data);
      });
  }

  if (!post) return "No post!"

  return (
    <div>
      <h1>{post.text}</h1>
      <p>{post.reverse}</p>
      <button onClick={createPost}>Create Post</button>
    </div>
  );
}
