import ReactDOM from "react-dom";
import styled from "styled-components";
import UIDropdown from "./ui_dropdown";

const Container = styled.div`
  display: flex;
  justify-content: flex-end;
`;

const UserCicle = styled.div`
  margin-right: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  border-radius: 50%;
  background: #34bf75;
  font-size: 12px;
  color: #fff;
  cursor: pointer;
`;

const Menu = styled.div`
  position: relative;
  width: 200px;
  background: #fff;
  box-shadow: 0 1px 2px 0 rgba(64, 80, 108, 0.2),
    0 0 24px -1px rgba(64, 80, 108, 0.2);
  border-radius: 8px;

  &:after {
    content: "";
    position: absolute;
    right: 10px;
    top: -14px;
    border: 7px solid transparent;
    border-bottom: 7px solid #fff;
  }
`;

const MenuItem = styled.div`
  padding: 16px;
  font-size: 14px;
  color: #657389;
  line-height: 1;
  cursor: pointer;

  &:not(:last-child) {
    border-bottom: 1px solid rgba(64, 80, 108, 0.1);
  }

  &:hover {
    background-color: rgba(245, 246, 248, 0.5);
    color: #40506c;
  }
`;

export default class Navbar1 extends React.Component {
  handleClick = callback => {
    console.log("MenuItem click");
    callback();
  };

  render() {
    return (
      <Container>
        <UIDropdown
          render={toggleShow => <UserCicle onClick={toggleShow}>U</UserCicle>}
          position={{
            top: "48px",
            right: "10px"
          }}
        >
          {toggleShow => (
            <Menu>
              <MenuItem onClick={() => this.handleClick(toggleShow)}>
                User settings
              </MenuItem>
              <MenuItem onClick={() => this.handleClick(toggleShow)}>
                Log out
              </MenuItem>
            </Menu>
          )}
        </UIDropdown>
      </Container>
    );
  }
}
