import { FiszkiCard } from '../education/FiszkiCard';
import styled from "styled-components";
import { List } from '../education/List';
import React from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const Wrapper = styled.main`
  padding: 2rem 0;
`;

export const Container = styled.div`
  width: 100%;
  max-width: 1240px;
  margin: 0 auto;
  padding: 0 2rem;
`;

class Fiszki extends React.Component {
    constructor(props) {
        super(props);

        this.state = {

            fiszki:[]
        }
    }


    componentDidMount(){
        axios.get('http://127.0.0.1:8000/api/fiszki/')
            .then(response => {
                console.log(response)
                this.setState({fiszki: response.data})
            })
            .catch(error  => {
                console.log(error)
            })



    }
    render() {
        const {fiszki} = this.state
        return(
            <>
            <Wrapper>
              <Container>
        
              <List>
                  {
                    fiszki.map((n) => {
                      const countryInfo = {
                      img: n.reverse,
                      text: n.text,
                      };
                      return (
                        <FiszkiCard
                      key={n.text}
                      onClick={() => useHistory(`/fiszki/${n.text}`)}
                      {...countryInfo}
                    />
                     );
                })}
              </List>
              </Container>
              </Wrapper>
              </>

        );
    }
}

export default Fiszki;
