import React from "react";
import ReactDOM from "react-dom";
import styled from "styled-components";
import UIDropdown from "./UIDropdown.js";
import { Colors, TextStyles } from "../../Theme";


const Container = styled.div`
  display: flex;
  justify-content: flex-end;
`;

const NavItem = styled.a`
   cursor: pointer;
   
  :visited {
    color: ${Colors.Brand.Text};
  }
`;


const Menu = styled.div`
  position: center;
  width: 100px;
  background: #fff;
  box-shadow: 0 1px 2px 0 rgba(64, 80, 108, 0.2),
    0 0 24px -1px rgba(64, 80, 108, 0.2);
  border-radius: 8px;

  &:after {
    content: "";
    position: center;
    right: 10px;
    top: -14px;
    border: 7px solid transparent;
    border-bottom: 7px solid #fff;
  }
`;

const MenuItem = styled.div`
  padding: 16px;
  font-size: 14px;
  color: #657389;
  line-height: 1;
  cursor: pointer;

  &:not(:last-child) {
    border-bottom: 1px solid rgba(64, 80, 108, 0.1);
  }

  &:hover {
    background-color: rgba(245, 246, 248, 0.5);
    color:${Colors.Brand.Text};
  }
`;

export default class Navbar1 extends React.Component {
  handleClick = callback => {
    console.log("MenuItem click");
    callback();
  };

  render() {
    return (
      <Container>
        <UIDropdown
          render={toggleShow => <NavItem onClick={toggleShow}>Stw√≥rz</NavItem>}
          position={{
            top: "48px",
            right: "5px"
          }}
        >
          {toggleShow => (
            <Menu>
              <MenuItem onClick={() => this.handleClick(toggleShow)}>
                Fiszka
              </MenuItem>
              <MenuItem onClick={() => this.handleClick(toggleShow)}>
                Kategoria
              </MenuItem>
            </Menu>
          )}
        </UIDropdown>
      </Container>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<Navbar1 />, rootElement);
