import React, { useState } from 'react';
import axios from 'axios';
import styled from "styled-components";
import Button from "../components/styled/Button.styled";
import Input from "../components/Inputs/Input";
import H3 from "../components/styled/H3.styled";
import { Colors, TextStyles } from "../Theme";
import { useHistory } from "react-router";
import { useDispatch } from "react-redux";
import authSlice from "../store/slices/Auth";
import { useFormik } from "formik";


const FormSection = styled.div`
	padding: 160px 0;
`;

const AppEl = styled.div`
  display: flex;
  ustify-content: center;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  height: 60vh;
  width: 30vw;
  background: rgba(255, 255, 255, 0.15);
  box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.37);
  backdrop-filter: blur(100rem);
  -webkit-backdrop-filter: blur(100rem);
  border-radius: 10px;
  color: #ffffff;
  text-transform: uppercase;
  letter-spacing: 0.4rem;
`;

const WelcomeText = styled(H3)`
  margin: 4rem 0 4rem 0;
  color: ${Colors.Brand.Text};
`;

const InputContainer = styled.div`
  row-gap: 1rem;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  align-items: center;
  height: 20%;
  width: 100%;
  text-color: ${Colors.Brand.Text};
`;

const ButtonContainer = styled.div`
  margin: 3rem 0 2rem 0;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
`;




class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: ''
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('TwÃ³j ulubiony smak to: ' + this.state.value);
    event.preventDefault();


    const user = {
      email: this.state.email,
      password: this.state.password
    };




    axios
      .post(`http://127.0.0.1:8000/api/auth/login/`, { user })
      .then((res) => {
        authSlice.actions.setAuthTokens({
          token: res.data.access,
          refreshToken: res.data.refresh,
        })
      console.log(res.data)
        authSlice.actions.setAccount(res.data.user)
        //setLoading(false);
        push("/");
      })
      .catch((err) => {
        console.log(err.res)

      });
  };

  render () {
    return (
      <FormSection>
        <AppEl>
          <WelcomeText>Zaloguj siÄ</WelcomeText>
         <form onSubmit={this.handleSubmit}>
            <InputContainer>
              <input
                id="email"
                type="email"
                placeholder="Email"
                name="email"

                onChange={this.handleChange}
              />
              <input
                id="password"
                type="password"
                placeholder="HasÅo"
                name="password"
                //value={this.state.value.password}
                onChange={this.handleChange}
              />

            </InputContainer>



            <ButtonContainer>
              <button
                type="submit"
                //disabled={loading}
                >
                Zaloguj siÄ
              </button>
            </ButtonContainer>

          </form>
        </AppEl>
    </FormSection>
    );
  }
}

export default Form;
