import styled from "styled-components";
import H4 from "../styled/H3.styled";
import Body from "../styled/S2.styled";
import Img from "../styled/Img.styled";
import H2 from "../../styled/H2.styled";
import S2 from "../../styled/S2.styled";

const StatItemEl = styled.span`
  display: flex;
  align-items: center;
  gap: 1rem;
  white-space: nowrap;
`;

const SubTitle = styled(S2)`
  text-align: center;
`;

export default function StatItem({ item }) {
  const { icon, title, text } = item;
  return (
    <StatItemEl>
      <Img src={icon} />
      <span>
      <H2>Co my oferujemy</H2>
      <SubTitle>
      jakiś tekst o sposobie  nauki języka migołego na naszej stronie
      </SubTitle>
        <H4>{title}</H4>
        <Body>{text}</Body>
      </span>
    </StatItemEl>
  );
}
