import React, { useState } from 'react';
import styled from "styled-components";
import Button from "../components/styled/Button.styled";
import Input from "../components/Inputs/Input";
import H3 from "../components/styled/H3.styled";
import { Colors, TextStyles } from "../Theme";
import { useHistory } from "react-router";
import { useDispatch } from "react-redux";
import { useFormik } from "formik";

const FormSection = styled.div`
	padding: 160px 0;
`;

const AppEl = styled.div`
  display: flex;
  ustify-content: center;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  height: 60vh;
  width: 30vw;
  background: rgba(255, 255, 255, 0.15);
  box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.37);
  backdrop-filter: blur(100rem);
  -webkit-backdrop-filter: blur(100rem);
  border-radius: 10px;
  color: #ffffff;
  text-transform: uppercase;
  letter-spacing: 0.4rem;
`;

const WelcomeText = styled(H3)`
  margin: 4rem 0 4rem 0;
  color: ${Colors.Brand.Text};
`;

const InputContainer = styled.div`
  row-gap: 1rem;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  align-items: center;
  height: 20%;
  width: 100%;
  text-color: ${Colors.Brand.Text};
`;

const ButtonContainer = styled.div`
  margin: 3rem 0 2rem 0;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
`;

function Form(){
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();
  const history = useHistory(); 

  const handleLogin = (email: string, password: string) => {
    //
  };
  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    onSubmit: (values) => {
      setLoading(true);
      handleLogin(values.email, values.password);
    },
  });


  onChange = e => {
    this.setState({ [e.target.name]: e.target.value});
  };



  login = event => {
    const results = fetch('http://127.0.0.1:8000/api/auth/login/', {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify(this.state.username, this.state.password)
    })


    results.then(function(results) {
      console.log(results)
    })


  }

 return (
    <FormSection>
    <AppEl>
      <WelcomeText>Zaloguj się</WelcomeText>
      <InputContainer>
      <form onSubmit={formik.handleSubmit}>
        <Input type="text" placeholder="Email" name="username" 
           value={this.state.username}
           onChange={formik.handleChange} />
        <Input type="password" placeholder="Hasło" name="password"
           value={this.state.password}
           onChange={formik.handleChange} />
        </form>
      </InputContainer>
      <ButtonContainer>
        <Button onClick={this.login}>Zaloguj się</Button>
      </ButtonContainer>
    </AppEl>
    </FormSection>
    );
 }


export default Form;
