import axios from "axios";
import React from "react";



export default function App() {

  const [post, setPost] = React.useState(null);



  React.useEffect(() => {
    axios.get('http://127.0.0.1:8000/api/fiszki/').then((response) => {
      setPost(response.data);
    });
  }, []);

  const handleRegister = (text,reverse) => {
    axios
      .post('http://127.0.0.1:8000/api/fiszki/', {text,reverse})
      .then((response) => {
        setPost(response.data);
      });
  }

  if (!post) return "No post!"

  const formik = useFormik({
    initialValues: {
      text: "",
      reverse: "",
    },
    onSubmit: (values) => {
      setLoading(true);
      handleRegister(values.text,values.reverse);
    },
  });

  return (
    <div>
        <form onSubmit={formik.handleSubmit}>
          <InputContainer>
            <StyledInput
              type="text"
              placeholder="Imię"
              name="text"
              value={formik.values.text}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
            />
            <StyledInput
              type="reverse"
              placeholder="Email"
              name="reverse"
              value={formik.values.reverse}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
            />
            {formik.errors.email ? 
            <div>
              {formik.errors.email} 
              </div> : null}
          </InputContainer>

          <ButtonContainer>
            <Button
              type="submit"
              disabled={loading}

            >
              Zarejestruj się
            </Button>
          </ButtonContainer>

        </form>
      <h1>{post.text}</h1>
      <p>{post.reverse}</p>

    </div>
  );
}
